    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try{
            Object valor = JOptionPane.showInputDialog("Ingresa el valor");
            if(valor == null)return;
            pila.push(valor);
            elementos.setListData(pila.getElementos());
        }catch(Exception e){
            JOptionPane.showMessageDialog(this, "Error al hacer el push.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        if(pila.size()==0){
            JOptionPane.showMessageDialog(this, "La pila esta vacia");
            return;
        }
        try{
            Object dato = pila.pop();
            elementos.setListData(pila.getElementos());
            JOptionPane.showMessageDialog(this, "Elemento desempilado: "+dato.toString());
        }catch(Exception e){
            JOptionPane.showMessageDialog(this, "Error al hacer el pop", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

