    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        if(pila.size()==0){
            JOptionPane.showMessageDialog(this, "La pila esta vacia");
            return;
        }
        try{
            Object dato = pila.peek();
            JOptionPane.showMessageDialog(this, "Elemento: "+dato.toString());
        }catch(Exception e){
            JOptionPane.showMessageDialog(this, "Error al tomar el dato", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton9ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton9ActionPerformed
        if(cola.size()==0){
            JOptionPane.showMessageDialog(this, "La cola esta vacia");
            return;
        }
        try{
            Object dato = cola.frente();
            JOptionPane.showMessageDialog(this, "Elemento: "+dato.toString());
        }catch(Exception e){
            JOptionPane.showMessageDialog(this, "Error al tomar el dato", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton9ActionPerformed

